package com.fjzxdz.ams.module.enviromonit.water.param;import com.fjzxdz.ams.module.enviromonit.water.entity.TaskDetalis;import com.fjzxdz.ams.module.enviromonit.water.entity.WtHourData;import cn.fjzxdz.frame.common.BaseQueryParam;import cn.fjzxdz.frame.common.SearchCondition;@SuppressWarnings("serial")public class TaskDetalisParam extends BaseQueryParam {		@SuppressWarnings({ "rawtypes", "unused" })	private Class clazz;		private String userName;		public TaskDetalisParam() {		super(TaskDetalis.class);		this.clazz = WtHourData.class;	}	@Override	protected void createQueryClauses() {			addClause("userName",getUserName(), SearchCondition.LIKE);		setOrderBy(" update_date desc");	}	public String getUserName() {		return userName;	}	public void setUserName(String userName) {		this.userName = userName;	}		}